import React, { Component } from "react";
import {
  Card,
  CardImg,
  CardImgOverlay,
  CardText,
  CardBody,
  CardTitle,
} from "reactstrap";

class DishDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  renderDish(dish) {
    if (dish != null)
      return (
        <Card>
          <CardImg top src={dish.image} alt={dish.name} />
          <CardBody>
            <CardTitle>{dish.name}</CardTitle>
            <CardText>{dish.description}</CardText>
          </CardBody>
        </Card>
      );
    else return <div></div>;
  }

  renderComments(dish) {
    // const listI = dish.comments.map((comment) => {
    //   return (
    //     <div>
    //       <h4>comments</h4>
    //       <div>{comment.id}</div>
    //     </div>
    //   );
    // });
    // return <div>{console.log(dish.comments)}</div>;

    if (dish != null)
      return (
        <div>
          <h4>Comments</h4>
          <ul className="list-group">
            <li className="list-group-item">
              {dish.comments[0].comment}
              <br />
              --{dish.comments[0].author}, {dish.comments[0].date}
            </li>
            <li className="list-group-item">
              {dish.comments[1].comment}
              <br />
              --{dish.comments[1].author}, {dish.comments[1].date}
            </li>
            <li className="list-group-item">
              {dish.comments[2].comment}
              <br />
              --{dish.comments[2].author}, {dish.comments[2].date}
            </li>
            <li className="list-group-item">
              {dish.comments[3].comment}
              <br />
              --{dish.comments[3].author}, {dish.comments[3].date}
            </li>
            <li className="list-group-item">
              {dish.comments[4].comment}
              <br />
              --{dish.comments[4].author}, {dish.comments[4].date}
            </li>
          </ul>
        </div>
      );
    else return <div></div>;
  }

  render() {
    return (
      <div className="row">
        <div className="col-12 col-md-5 m-1">
          {this.renderDish(this.props.selectedDish)}
        </div>

        <div className="col-12 col-md-5 m-1">
          {this.renderComments(this.props.selectedDish)}
        </div>
      </div>
    );
  }
}

export default DishDetail;
