import React, { Component } from "react";
import {
  Card,
  CardImg,
  CardImgOverlay,
  CardText,
  CardBody,
  CardTitle,
} from "reactstrap";

class DishDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  renderDish(dish) {
    if (dish != null)
      return (
        <Card>
          <CardImg top src={dish.image} alt={dish.name} />
          <CardBody>
            <CardTitle>{dish.name}</CardTitle>
            <CardText>{dish.description}</CardText>
          </CardBody>
        </Card>
      );
    else return <div></div>;
  }

  renderComments(dish) {
    // const listI = dish.comments.map((comment) => {
    //   return (
    //     <div>
    //       <h4>comments</h4>
    //       <div>{comment.id}</div>
    //     </div>
    //   );
    // });
    // return <div>{console.log(dish.comments)}</div>;

    if (dish != null)
      return (
        <div>
          <h4>Comments</h4>
          {console.log(dish.comments)}
          <div>{dish.comments[0].comment}</div>
          <div>
            --{dish.comments[0].author}, {dish.comments[0].date}
          </div>
        </div>
      );
    else return <div></div>;
  }

  render() {
    const dis = this.props.selectedDish;
    return (
      <div className="row">
        <div className="col-12 col-md-5 m-1">
          {this.renderDish(this.props.selectedDish)}
        </div>

        <div className="col-12 col-md-5 m-1">
          {this.renderComments(this.props.selectedDish)}
        </div>
      </div>
    );
  }
}

export default DishDetail;
